import React, { Component } from 'react'

class AddTodo extends Component {
    constructor(props)
    {
        super(props);
        this.state={
            title:''
        }
    }

    onChange=()=>
    {
        this.setState({
            title:this.refs
        })
    }
    render() {
        return (
            <div>
                <form style={{display:'flex'}}>
                    <input type="text" name="title" placeholder="Type the item..."style={{flex:10}} onChange={this.onChange}/>
                    <input type="submit" value="Add Item"  className="btn" style={{flex:2}} />
                </form>
            </div>
        )
    }
}

export default AddTodo
