import React, { Component } from 'react'

class TodoItem extends Component {
    getStyle=()=>
    {
        if(this.props.item.completed)
        {
            return (
                {
                    textDecoration : 'line-through'
                }
            );
        }
        else{
            return(
                {
                    textDecoration: 'none'
                }
            )
        }
    }
    render() {
        return (
            <div>
                <ul>
        <li style={this.getStyle()}>{this.props.item}</li>
                </ul>
            </div>
        )
    }
}

export default TodoItem
