import React, { Component } from 'react'

class TodoItem extends Component {
    getStyle=()=>
    {
        // if(this.props.item.completed)
        // {
        //     return (
        //         {
        //             textDecoration : 'line-through'
        //         }
        //     );
        // }
        // else{
        //     return(
        //         {
        //             textDecoration: 'none'
        //         }
        //     )
        // }

        //by using ternery operator
        return({
            backgroundColor:'#f4f4f4',
            textDecoration : this.props.item.completed?'line-through':'none',

        })
    }
    render() {

        return (
            <div>    
        <h1 style={this.getStyle()}><input type="checkbox" onChange={this.props.markComplete.bind(this,this.props.item.id)}/>{' '}{this.props.item.title}{' '}<button onClick={this.props.delTodo.bind(this,this.props.item.id)} style={{backgroundColor:'Red',color:'white'}}>X</button></h1>
            </div>
        )
    }
}

export default TodoItem
